            var where = 'bottom';
            var field = this.field;
            if (position !== undefined && position !== null) {
                if (typeOf(position) == 'integer' &&
                    (position >= 0  && position < field.options.length)) {
                    field = this.field.options[position];
                    where = 'before';
                } else if (position == 'top') {
                    where = 'top';
                }
    
            }
            opt.inject(field, where);
