                item.addEvents({
                    add: function(what) { 
                        this.fireEvent('add', what); 
                    }.bind(this),
                    remove: function(what) { 
                        this.fireEvent('remove', what); 
                    }.bind(this)
                    /*
                    click: function(what) { 
                        this.fireEvent('click', what); 
                    }.bind(this)
                    */
                });
